void _lhz    ( uint5_t rD , sint16_t d  , uint5_t rA ) { _MEMORY( OP_LOAD  , ARG_UIMM , QUAL_NONE      , false , false , SIZE_HALFWORD , rD , 0  , rA , d  , 0  ); }
