   urgent chan _doTerminate;
   urgent chan _do;

bool _mustTerminate;
int[-1,  _INST_MAX -1] _Stack[_STACK_MAX];
int[ 0, _STACK_MAX   ] _stack_top;

_M_Pred_t _M_StaticPred;
_M_Pred_t _M_DynamicPred;
int       _M_BTBOverwriting;

