void _lhzux  ( uint5_t rD , uint5_t  rA , uint5_t rB ) { _MEMORY( OP_LOAD  , ARG_REG  , QUAL_NONE      , true  , true  , SIZE_HALFWORD , rD , 0  , rA , 0  , rB ); }
