void _andc_   ( uint5_t rA , uint5_t rS , uint5_t  rB   ) { _LOGICAL( OP_AND  , ARG_REG  , QUAL_COMPLEMENT , SIZE_NONE     , rA , rS , rB , 0    , false , true  ); }
