  /* 32 */    0,  110,    0,  115, /* 36 */    0,  120,    0,  119, /* 40 */    0,  118,    0,   56, /* 44 */    0,   56,    0,   56, 
  /* 48 */    0,  119,    0,  121, /* 52 */    0,  122,    0,  120, /* 56 */    0,  120,    0,   59, /* 60 */    0,   59,    0,   59, 
  /* 64 */    0,  119,    0,  120, /* 68 */    0,  121,    0,  122, /* 72 */    0,  122,    0,   55, /* 76 */    0,   55,    0,   55, 
  /* 80 */    0,  121,    0,  121, /* 84 */    0,  121,    0,  121, /* 88 */    0,   60,    0,   57, /* 92 */    0,   57,    0,   57, 
